t.script:
  document.addEventListener('DOMContentLoaded', function () {
  const yearSelect = document.getElementById('yearSelect');
  const monthSelect = document.getElementById('monthSelect');
  const dayView = document.getElementById('dayView');
  const resetBtn = document.getElementById('resetBtn');

  const chartDom = document.getElementById('my_chart');
  const chart = echarts.init(chartDom);

  // Initial chart render with no year/month selected


  // Disable month until year is selected
  monthSelect.disabled = true;


  // Fetch years
  fetch('/store_product_batch/get_years', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({})
  })
    .then(res => res.json())
    .then(data => {
      console.log("*******************5")
      console.log(data)
      yearSelect.innerHTML = '';

      const placeholder = document.createElement('option');
      placeholder.value = '';
      placeholder.textContent = '-- Select Year --';
      placeholder.disabled = true;
      placeholder.selected = true;
      yearSelect.appendChild(placeholder);

      data.result.years.forEach(year => {
        const option = document.createElement('option');
        option.value = year;
        option.textContent = year;
        yearSelect.appendChild(option);
      });

      updateChartWithDateContext(null, null);
    });



  // Year selection
  yearSelect.addEventListener('change', function () {
    const selectedYear = this.value;
    monthSelect.innerHTML = '';
    monthSelect.disabled = true;

    if (!selectedYear) {
      updateChartWithDateContext(null, null);
      return;
    }

    fetch('/store_product_batch/get_months', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ year: selectedYear })
    })
      .then(res => res.json())
      .then(data => {
        console.log("working ......")
        const placeholder = document.createElement('option');
        placeholder.value = '';
        placeholder.textContent = '-- Select Month --';
        placeholder.disabled = true;
        placeholder.selected = true;
        monthSelect.appendChild(placeholder);

        data.result.months.forEach(month => {
          const option = document.createElement('option');
          option.value = month;
          option.textContent = month;
          monthSelect.appendChild(option);
        });

        monthSelect.disabled = false;
        updateChartWithDateContext(selectedYear, null);
      });
  });

  // Month selection
  monthSelect.addEventListener('change', function () {
    const selectedMonth = this.value;
    const selectedYear = yearSelect.value;
    if (!selectedMonth || !selectedYear) return;
    updateChartWithDateContext(selectedYear, selectedMonth);
  });

  // Reset
  resetBtn.addEventListener('click', function () {
    yearSelect.value = '';
    monthSelect.innerHTML = '';
    monthSelect.disabled = true;
    updateChartWithDateContext(null, null);
  });

  function updateChartWithDateContext(year, month) {
    console.log("calling the updateChartWithDateContext")

    fetch('/store_product_batch/chart_data', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({"payload" : { year, month }})  
    })
      .then(response => response.json())
      .then(option => {
        console.log("****************4")
        console.log(option)
        chart.setOption(option.result);
      })
      .catch(error => {
        console.error('Failed to load chart data:', error);
      });
  }
});
